import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import BranchFinder from "../api/BranchFinder"; // Ensure this import is correct and adjust path if necessary

const UpdateBranch = () => {
  const { branchID } = useParams();
  const navigate = useNavigate();
  const [branchName, setBranchName] = useState("");
  const [managerID, setManagerID] = useState("");

  useEffect(() => {
    const fetchBranch = async () => {
      try {
        const response = await BranchFinder.get(`/branch/${branchID}`);
        console.log(response.data);  // Log the response data to verify structure
        if (response.data) {
          setBranchName(response.data.branch_name);
          setManagerID(response.data.manager_id);
        }
      } catch (err) {
        console.error("Error fetching branch details:", err);
      }
    };
    fetchBranch();
  }, [branchID]);  // Ensure useEffect is called when branchID changes



  return (
    <div className="container">
      <form>
        <div className="form-group">
          <label htmlFor="branch_name">Branch Name</label>
          <input
            value={branchName}
            onChange={e => setBranchName(e.target.value)}
            id="branch_name"
            className="form-control"
            type="text"
          />
        </div>
        <div className="form-group">
          <label htmlFor="manager_id">Manager ID</label>
          <input
            value={managerID}
            onChange={e => setManagerID(e.target.value)}
            id="manager_id"
            className="form-control"
            type="text"
          />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
};

export default UpdateBranch;
