import React, { useState, useContext } from "react";
import BranchFinder from "../api/BranchFinder"; // Ensure this is correct
import { BranchContext } from "../context/BranchContext";

const AddBranch = () => {
  const { addBranches } = useContext(BranchContext); // Ensure you have addBranches function in your context
  const [branchName, setBranchName] = useState("");
  const [managerID, setManagerID] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault(); // Correctly calling the function
    try {
      const response = await BranchFinder.post("/", {
        branchName,
        managerID
      });
      console.log(response.data.data);
      addBranches(response.data.data.branch);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="mb-4">
      <form onSubmit={handleSubmit}> {/* Changed from onClick to onSubmit */}
        <div className="form-row">
          <div className="col">
            <input
              value={branchName}
              onChange={(e) => setBranchName(e.target.value)}
              type="text"
              className="form-control"
              placeholder="Branch Name"
            />
          </div>
          <div className="col">
            <input
              value={managerID}
              onChange={(e) => setManagerID(e.target.value)}
              type="text"
              className="form-control"
              placeholder="Manager ID"
            />
          </div>
          <button
            type="submit"
            className="btn btn-primary"
          >
            Add Branch
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddBranch;
