import React, { useState } from "react";
import { useParams } from "react-router-dom";

const UpdateBranch = () => {
  const { branchID } = useParams();
  const [branchName, setBranchName] = useState("");  // Corrected
  const [managerID, setManagerID] = useState("");    // Corrected

  useEffect(() => {
    const fetchData = async() => {
      const response = await BranchFinder.get(`/{branch_id}`)
      console.log(response.data.data)
      setBranchName(response.data.data.branch_name)
      setManagerID(response.data.data.manager_id)
    }
    fetchData()
  }, [])


  return (
    <div className="container">
      <form action="">
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="branchName">Branch Name</label>
          <input 
            value={branchName} 
            onChange={e => setBranchName(e.target.value)} 
            id="branch_name" 
            className="form-control" 
            type="text" 
          />
        </div>
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="managerID">Manager ID</label>
          <input 
            value={managerID} 
            onChange={e => setManagerID(e.target.value)} 
            id="manager_id" 
            className="form-control" 
            type="number" 
          />
        </div>
        <button className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
}

export default UpdateBranch;
