import React, { useState } from 'react';
import BranchList from "../components/BranchList";
import EmployeeList from "../components/EmployeeList";
import ClientList from "../components/ClientList";
import ProjectList from "../components/ProjectList";
import AddBranch from "../components/AddBranch";
import AddEmployee from "../components/AddEmployee";
import AddClient from "../components/AddClient";
import AddProject from "../components/AddProject";

const Home = () => {
  const [layout, setLayout] = useState('horizontal'); // default layout

  const toggleLayout = () => {
    setLayout(layout === 'horizontal' ? 'vertical' : 'horizontal');
  };

  return (
    <div>
      <button onClick={toggleLayout}>Toggle Layout</button>
      <div className={layout === 'horizontal' ? 'home-container horizontal' : 'home-container vertical'}>
        <div className="section Branch">
          <h1 className="text-center">Branch</h1>
          <BranchList />
          <AddBranch />
        </div>
        <div className="section Employee">
          <h1 className="text-center">Employee</h1>
          <EmployeeList />
          <AddEmployee />
        </div>
        <div className="section Client">
          <h1 className="text-center">Client</h1>
          <ClientList />
          <AddClient />
        </div>
        <div className="section Project">
          <h1 className="text-center">Project</h1>
          <ProjectList />
          <AddProject />
        </div>
      </div>
    </div>
  );
};

export default Home;
