import React, {useContext, useEffect} from "react"
import BranchFinder from "../api/BranchFinder"
import { BranchContext } from "../context/BranchContext"

const BranchList = (props) => {
  const {branch, setBranch} = useContext(BranchContext)
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await BranchFinder.get("/");  // This hits "http://localhost:4700/branch/"
        if (response && response.data) {
          setBranch(response.data); // Assuming the server just returns an array
        }
      } catch (err) {
        console.error("Failed to fetch branch data:", err);
      }
    };
    fetchData();
  }, [setBranch])  // Dependency array is correct now


  return (
    <div className="container">
      <table className="table table-hover">
        <thead>
          <tr className="table-active">
            <th scope="col">Branch ID</th>
            <th scope="col">Branch Name</th>
            <th scope="col">Manager ID</th>
          </tr>
        </thead>
        <tbody>
        {branch.map(branch => {
            return (
            <tr>
              <td>{branch.branch_id}</td>
              <td>{branch.branch_name}</td>
              <td>{branch.manager_id}</td>
            </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  )
}

export default BranchList
