import React, { useState, useEffect } from "react"; // Import useEffect here
import { useParams } from "react-router-dom";
import BranchFinder from "../api/BranchFinder"; // Make sure the path is correct

const UpdateBranch = () => {
  const { branchID } = useParams();
  const [branchName, setBranchName] = useState("");
  const [managerID, setManagerID] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Use template literals for the URL and ensure the variable part is properly enclosed in braces
      const response = await BranchFinder.put(`/${branchID}`, {
        branch_name: branchName,
        manager_id: managerID
      });

      // Log the response after the PUT request is made
      console.log(response);

      // Assuming you might want to handle the response further, like updating the UI or redirecting
    } catch (error) {
      // It's good to handle errors explicitly
      console.error("Failed to update the branch:", error);
    }
  };

  return (
    <div className="container">
      <form action="">
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="branchName">Branch Name</label>
          <input 
            value={branchName} 
            onChange={e => setBranchName(e.target.value)} 
            id="branch_name" 
            className="form-control" 
            type="text" 
          />
        </div>
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="managerID">Manager ID</label>
          <input 
            value={managerID} 
            onChange={e => setManagerID(e.target.value)} 
            id="manager_id" 
            className="form-control" 
            type="number" 
          />
        </div>
        <button type="submit" onClick={handleSubmit} className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
}

export default UpdateBranch;
