import React, { useState, useEffect } from "react"; // Import useEffect here
import { useParams } from "react-router-dom";
import BranchFinder from "../api/BranchFinder"; // Make sure the path is correct

const UpdateBranch = () => {
  const { branchID } = useParams();
  const [branchName, setBranchName] = useState("");
  const [managerID, setManagerID] = useState("");

  useEffect(() => {
    const fetchData = async() => {
      try {
        const response = await BranchFinder.get(`/${branchID}`);
        setBranchName(response.data.data.branch_name);
        setManagerID(response.data.data.manager_id);
      } catch (error) {
        if (error.response) {
          // The request was made and the server responded with a status code
          // that falls out of the range of 2xx
          console.error("Error response data:", error.response.data);
          console.error("Error response status:", error.response.status);
          console.error("Error response headers:", error.response.headers);
        } else if (error.request) {
          // The request was made but no response was received
          console.error("Error request:", error.request);
        } else {
          // Something happened in setting up the request that triggered an Error
          console.error("Error message:", error.message);
        }
      }
    };

    fetchData();
  }, [branchID]);


  return (
    <div className="container">
      <form action="">
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="branchName">Branch Name</label>
          <input 
            value={branchName} 
            onChange={e => setBranchName(e.target.value)} 
            id="branch_name" 
            className="form-control" 
            type="text" 
          />
        </div>
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="managerID">Manager ID</label>
          <input 
            value={managerID} 
            onChange={e => setManagerID(e.target.value)} 
            id="manager_id" 
            className="form-control" 
            type="number" 
          />
        </div>
        <button className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
}

export default UpdateBranch;
