import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import BranchFinder from "../api/BranchFinder";

const UpdateBranch = () => {
  const { branchID } = useParams(); // Ensure the param name matches your route definition
  const navigate = useNavigate();
  const [branchName, setBranchName] = useState("");
  const [managerID, setManagerID] = useState("");

  useEffect(() => {
    const fetchBranch = async () => {
      try {
        const response = await BranchFinder.get(`/branch/${branchID}`); // Adjust API endpoint as needed
        if (response.data && response.data.branch) { // Make sure to adjust according to your actual response structure
          setBranchName(response.data.branch.branch_name);
          setManagerID(response.data.branch.manager_id);
        }
      } catch (err) {
        console.error("Error fetching branch details:", err);
      }
    };
    fetchBranch();
  }, [branchID]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updatedData = {
        branch_name: branchName,
        manager_id: managerID
      };
      const response = await BranchFinder.put(`/${branchID}`, updatedData);
      if (response.status === 200) {
        navigate("/"); // Redirect after successful update
      }
    } catch (err) {
      console.error("Failed to update the branch:", err);
    }
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="branch_name">Branch Name</label>
          <input
            value={branchName}
            onChange={e => setBranchName(e.target.value)}
            id="branch_name"
            className="form-control"
            type="text"
          />
        </div>
        <div className="form-group">
          <label htmlFor="manager_id">Manager ID</label>
          <input
            value={managerID}
            onChange={e => setManagerID(e.target.value)}
            id="manager_id"
            className="form-control"
            type="text"
          />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
};

export default UpdateBranch;
