import React, { useState, useEffect } from "react"; // Import useEffect here
import { useParams } from "react-router-dom";
import BranchFinder from "../api/BranchFinder"; // Make sure the path is correct

const UpdateBranch = () => {
  const { branchID } = useParams();
  const [branchName, setBranchName] = useState("");
  const [managerID, setManagerID] = useState("");

  useEffect(() => {
    const fetchData = async() => {
      // Make sure to use template literals with backticks for variables inside strings
      const response = await BranchFinder.get(`/${branchID}`); // Corrected to use branchID from params
      console.log(response.data.data); // Debug the response if necessary
      setBranchName(response.data.data.branch_name);
      setManagerID(response.data.data.manager_id);
    }
    fetchData();
  }, [branchID]); // It's good practice to include variables used in useEffect in the dependency array

  return (
    <div className="container">
      <form action="">
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="branchName">Branch Name</label>
          <input 
            value={branchName} 
            onChange={e => setBranchName(e.target.value)} 
            id="branch_name" 
            className="form-control" 
            type="text" 
          />
        </div>
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="managerID">Manager ID</label>
          <input 
            value={managerID} 
            onChange={e => setManagerID(e.target.value)} 
            id="manager_id" 
            className="form-control" 
            type="number" 
          />
        </div>
        <button className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
}

export default UpdateBranch;
