import React, { useState, useContext, useEffect } from "react"
import { useParams } from "react-router-dom"
import { BranchContext } from "../context/BranchContext"
import BranchFinder from "../api/BranchFinder"

const UpdateBranch = (props) => {
  const {branchID} = useParams()
  const {branches} = useContext(BranchContext)
  const [branchName, setBranchName] = useState("")
  const [managerID, setManagerID] = useState("")

  useEffect(() => {
    const fetchData = async() => {
      const response = await BranchFinder.get(`/branch/${branchID}`)
      console.log(response.data.data.branch)
      setBranchName(response.data.data.branch_name)
      setManagerID(response.data.data.managerID)
    }
    fetchData();
  }, [])

  return <div className="container">
      <h1>{branches[0].branch_name}</h1>
      <form action="">
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="branch_name">Branch Name</label>
          <input 
            value={branchName} 
            onChange={e => setBranchName(e.target.value)} 
            branchID="branch_name" 
            className="form-control" 
            type="text"
          />
        </div>
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="manager_id">Manager ID</label>
          <input 
            value={managerID}
            onChange={e => setManagerID(e.target.value)} 
            branchID="manager_id" 
            className="form-control" 
            type="text"
          />
        </div>
        <button className="btn btn-primary">Submit</button>
      </form>
    </div>


}

export default UpdateBranch
