import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ProjectFinder from "../api/ProjectFinder";

const UpdateProject = () => {
  const { project_id } = useParams();
  const navigate = useNavigate();
  const [clientID, setClientID] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [description, setDescription] = useState("");
  const [branchID, setBranchID] = useState("");

  useEffect(() => {
    const fetchProject = async () => {
      try {
        const response = await ProjectFinder.get(`/${project_id}`);
        if (response.data) {
          setClientID(response.data.client_id);
          setStartDate(response.data.start_date);
          setEndDate(response.data.end_date);
          setDescription(response.data.description);
          setBranchID(response.data.branch_id);
        }
      } catch (err) {
        console.error("Error fetching project details:", err);
      }
    };
    fetchProject();
  }, [project_id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updatedProject = await ProjectFinder.put(`/${project_id}`, {
        client_id: clientID,
        start_date: startDate,
        end_date: endDate,
        description,
        branch_id: branchID
      });
      if (updatedProject.status === 200) {
        navigate("/");
      }
    } catch (err) {
      console.error("Failed to update the project:", err);
    }
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="clientID">Client ID</label>
          <input
            value={clientID}
            onChange={(e) => setClientID(e.target.value)}
            id="client_id"
            className="form-control"
            type="text"
          />
        </div>
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="startDate">Start Date</label>
          <input
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            id="start_date"
            className="form-control"
            type="text"
          />
        </div>
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="endDate">End Date</label>
          <input
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            id="end_date"
            className="form-control"
            type="text"
          />
        </div>
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="description">Description</label>
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            id="description"
            className="form-control"
            type="text"
          />
        </div>
        <div className="form-group" style={{ paddingBottom: '20px' }}>
          <label htmlFor="branchID">Branch ID</label>
          <input
            value={branchID}
            onChange={(e) => setBranchID(e.target.value)}
            id="branch_id"
            className="form-control"
            type="text"
          />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
};

export default UpdateProject;
